namespace IxMilia.Stl
{
    public struct StlVertex
    {
        public float X;
        public float Y;
        public float Z;

        public StlVertex(float x, float y, float z)
            : this()
        {
            X = x;
            Y = y;
            Z = z;
        }
    }
}
